mapper.select:
select notice_id as noticeId, title, content, create_date as createDate, views
from notice
where notice_id = #{noticeId}

select save_folder, original_file, save_file
from file_info
where notice_id = #{noticeId}

mapper.update:
update notice
set views = #{viewCount}
where notice_id = #{noticeId}

update notice
set title = #{title}, content = #{content}
where notice_id = #{noticeId}
