mapper.select:
select title, addr1, travel_info_id , first_image, latitude, longitude from travel_info
where sido_code = #{sidoCode}
and gugun_code = #{gugunCode}
and travel_type_id = #{travelTypeId}
order by readcount desc

mapper.update:
update travel_status
set star = #{star, jdbcType=DOUBLE}
where travel_info_id = #{travelInfoId}
  and user_id = #{userId}

mapper.select:
select title, addr1, first_image, travel_type_id, travel_info_id
from travel_info
where travel_info_id = #{travelInfoId}

select overview
from travel_description
where travel_description_id = #{travelInfoId}
