mapper.select:
		select name, user_id, email
		from user
		where user_id = #{userId}
          and password = #{password}

select name, user_id, email, sido_code
from user
where user_id = #{userId}

select user_id, name
from user
where user_id = #{userId}
  and password = #{password}

mapper.update:
UPDATE user
SET email = #{email}, sido_code = #{sidoCode}
WHERE user_id = #{userId} and password = #{password}
